#include <time.h>
#include <stdio.h>

void swap(int *a, int *b)
{
    int temp = *a;
    *a = *b;
    *b = temp;
}

void quickSort(int A[], int lowerBound, int upperBound)
{
    if (lowerBound < upperBound)
    {
        int pivot = partition(A, lowerBound, upperBound);

        // printf("\nbefore quick");
        quickSort(A, lowerBound, pivot - 1);
        quickSort(A, pivot + 1, upperBound);
        // printf("\nafter quick");
    }
}

int randomPivot(int lowerBound, int upperBound)
{
    int randomNum;
    // take current time as seed to generate random number
    srand(time(0));
    // randomNum should be within the range [lowerBound, upperBound]
    randomNum = (rand() % (upperBound - lowerBound)) + lowerBound;
    return randomNum;
}

int partition(int A[], int lowerBound, int upperBound)
{
    int start = lowerBound, end = upperBound;
    // pivot is at random position between lowerBound and upperBound
    int pivotPosition = randomPivot(lowerBound, upperBound);
    int pivot = A[pivotPosition];

    while (start < end)
    {
        while (A[start] <= pivot && (start < end))
        {
            start++;
        }
        while (A[end] > pivot)
        {
            end--;
        }
        if (start < end)
        {
            swap(&A[start], &A[end]);
        }
    }
    swap(&A[pivotPosition], &A[end]);
    return end;
}

